import { useEffect, useState } from 'react';
import zoomSdk from '@zoom/appssdk';
import SidePanelLayout from '../components/SidePanelLayout';
import CompactSuggestionPanel from '../components/CompactSuggestionPanel';

export default function ZoomApp() {
  const [meetingInfo, setMeetingInfo] = useState<any>(null);
  const [isConnected, setIsConnected] = useState(false);
  
  useEffect(() => {
    zoomSdk.config({ 
      capabilities: ['getMeetingContext', 'getMeetingParticipants'] 
    }).then(() => {
      setIsConnected(true);
      setTimeout(() => {
        setMeetingInfo({
          meetingUUID: 'zoom-123',
          participantCount: 6,
          isHost: false
        });
      }, 1000);
    }).catch(error => {
      console.error('Zoom SDK initialization failed:', error);
      // For demo purposes, still show connected state
      setTimeout(() => {
        setIsConnected(true);
        setMeetingInfo({
          meetingUUID: 'zoom-demo-123',
          participantCount: 6,
          isHost: false
        });
      }, 1000);
    });
  }, []);

  return (
    <SidePanelLayout 
      platform="zoom" 
      isConnected={isConnected} 
      meetingInfo={meetingInfo}
    >
      <CompactSuggestionPanel platform="zoom" />
    </SidePanelLayout>
  );
}
