import { useEffect, useState } from 'react';
import SidePanelLayout from '../components/SidePanelLayout';
import CompactSuggestionPanel from '../components/CompactSuggestionPanel';

export default function MeetApp() {
  const [meetingInfo, setMeetingInfo] = useState<any>(null);
  const [isConnected, setIsConnected] = useState(false);
  
  useEffect(() => {
    // Initialize Google Meet integration
    if (typeof google !== 'undefined' && google.apps) {
      google.apps.script.run
        .withSuccessHandler((result: any) => {
          console.log('Meet initialized:', result);
          setIsConnected(true);
          setMeetingInfo({
            ...result,
            participantCount: 5
          });
        })
        .withFailureHandler((error: any) => {
          console.error('Failed to initialize Meet:', error);
          // For demo purposes, still show connected state
          setTimeout(() => {
            setIsConnected(true);
            setMeetingInfo({
              meetingCode: 'abc-defg-hij',
              participantCount: 5,
              isHost: false
            });
          }, 1000);
        })
        .getMeetingInfo();
    } else {
      // Simulate Meet context for demo
      setTimeout(() => {
        setIsConnected(true);
        setMeetingInfo({
          meetingCode: 'abc-defg-hij',
          calendarEventId: 'calendar-event-123',
          organizer: 'user@company.com',
          isGoogleWorkspace: true,
          participantCount: 5,
          isHost: false
        });
      }, 1000);
    }
  }, []);

  return (
    <SidePanelLayout 
      platform="meet" 
      isConnected={isConnected} 
      meetingInfo={meetingInfo}
    >
      <CompactSuggestionPanel platform="meet" />
    </SidePanelLayout>
  );
}
