import { useState } from 'react';
import { Select, Card, Space, Button, Typography, Badge } from 'antd';
import ZoomApp from './zoom/ZoomApp';
import TeamsApp from './teams/TeamsApp';
import MeetApp from './meet/MeetApp';

const { Title, Text } = Typography;

function App() {
  const path = window.location.pathname;
  const [selectedPlatform, setSelectedPlatform] = useState<string>('zoom');

  // Platform-specific routing for production
  if (path.includes('zoom')) return <ZoomApp />;
  if (path.includes('teams')) return <TeamsApp />;
  if (path.includes('meet')) return <MeetApp />;

  // Development mode: Platform selector with demo scenarios
  const renderPlatformApp = () => {
    switch (selectedPlatform) {
      case 'zoom':
        return <ZoomApp />;
      case 'teams':
        return <TeamsApp />;
      case 'meet':
        return <MeetApp />;
      default:
        return <ZoomApp />;
    }
  };

  const handleUrlNavigation = (platform: string) => {
    window.history.pushState({}, '', `/${platform}`);
    window.location.reload();
  };

  return (
    <div style={{ padding: 20, maxWidth: 1200, margin: '0 auto' }}>
      {/* Demo Header */}
      <Card 
        title={
          <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
            <span>ðŸŽ¯ Clarivue Addon - Interactive Demo</span>
            <Badge status="processing" text="Frontend Only" />
          </div>
        } 
        style={{ marginBottom: 20 }}
      >
        <Space direction="vertical" style={{ width: '100%' }}>
          <Text>
            Experience our AI-powered meeting enhancement across different platforms. 
            All data is simulated for demonstration purposes.
          </Text>
          
          <Space wrap>
            <Text strong>Choose Platform:</Text>
            <Select
              value={selectedPlatform}
              onChange={setSelectedPlatform}
              style={{ width: 180 }}
              options={[
                { value: 'zoom', label: 'ðŸ“¹ Zoom Meeting' },
                { value: 'teams', label: 'ðŸ’¼ Microsoft Teams' },
                { value: 'meet', label: 'ðŸŽ¥ Google Meet' }
              ]}
            />
            
            <Button 
              size="small" 
              onClick={() => handleUrlNavigation(selectedPlatform)}
            >
              Test URL Routing
            </Button>
          </Space>
          
          <Text type="secondary" style={{ fontSize: 12 }}>
            ðŸ’¡ Each platform shows different UI patterns and SDK integrations
          </Text>
        </Space>
      </Card>
      
      {/* Platform App */}
      {renderPlatformApp()}
    </div>
  );
}

export default App;
