import { useEffect, useState } from 'react';
import { Card, Badge, Space, Typography } from 'antd';
import SuggestionPanel from '../components/SuggestionPanel';
import { getPlatformFeatures } from '../utils/mockData';

const { Text, Title } = Typography;

export default function MeetApp() {
  const [meetContext, setMeetContext] = useState<any>(null);
  const [isConnected, setIsConnected] = useState(false);
  
  useEffect(() => {
    // Initialize Google Meet integration
    if (typeof google !== 'undefined' && google.apps) {
      google.apps.script.run
        .withSuccessHandler((result: any) => {
          console.log('Meet initialized:', result);
          setIsConnected(true);
          setMeetContext(result);
        })
        .withFailureHandler((error: any) => {
          console.error('Failed to initialize Meet:', error);
        })
        .getMeetingInfo();
    } else {
      // Simulate Meet context for demo
      setTimeout(() => {
        setIsConnected(true);
        setMeetContext({
          meetingCode: 'abc-defg-hij',
          calendarEventId: 'calendar-event-123',
          organizer: 'user@company.com',
          isGoogleWorkspace: true
        });
      }, 1000);
    }
  }, []);

  const platformFeatures = getPlatformFeatures('meet');

  return (
    <div style={{ padding: 16 }}>
      {/* Google Meet-specific header */}
      <Card style={{ marginBottom: 16 }}>
        <Space direction="vertical" style={{ width: '100%' }}>
          <Space>
            <Title level={3} style={{ margin: 0 }}>üé• Google Meet Assistant</Title>
            <Badge 
              status={isConnected ? 'success' : 'warning'} 
              text={isConnected ? 'Connected' : 'Limited Features'} 
            />
          </Space>
          
          <Space wrap>
            <Text type="secondary">Platform: Google Meet</Text>
            {meetContext && (
              <>
                <Text type="secondary">‚Ä¢</Text>
                <Text type="secondary">Code: {meetContext.meetingCode}</Text>
                <Text type="secondary">‚Ä¢</Text>
                <Text type="secondary">Workspace: {meetContext.isGoogleWorkspace ? 'Yes' : 'No'}</Text>
              </>
            )}
          </Space>
          
          <Space wrap>
            <Text style={{ fontSize: 12 }}>‚úÖ Calendar integration</Text>
            <Text style={{ fontSize: 12 }}>‚úÖ Google Workspace</Text>
            <Text style={{ fontSize: 12 }}>‚ö†Ô∏è Limited participant data</Text>
            <Text style={{ fontSize: 12 }}>‚ö†Ô∏è No screen share detection</Text>
          </Space>
        </Space>
      </Card>

      <SuggestionPanel platform="meet" />
    </div>
  );
}
