import { useEffect, useState } from 'react';
import { Card, Badge, Space, Typography } from 'antd';
import zoomSdk from '@zoom/appssdk';
import SuggestionPanel from '../components/SuggestionPanel';
import { getPlatformFeatures } from '../utils/mockData';

const { Text, Title } = Typography;

export default function ZoomApp() {
  const [meetingInfo, setMeetingInfo] = useState<any>(null);
  const [isConnected, setIsConnected] = useState(false);
  
  useEffect(() => {
    // Initialize Zoom SDK
    zoomSdk.config({ 
      capabilities: ['getMeetingContext', 'getMeetingParticipants', 'getMeetingUUID'] 
    }).then(() => {
      setIsConnected(true);
      
      // Simulate getting meeting context
      setTimeout(() => {
        setMeetingInfo({
          meetingUUID: 'demo-zoom-meeting-123',
          participantCount: 6,
          isHost: false,
          isRecording: false
        });
      }, 1000);
    }).catch(error => {
      console.error('Zoom SDK initialization failed:', error);
    });
  }, []);

  const platformFeatures = getPlatformFeatures('zoom');

  return (
    <div style={{ padding: 16 }}>
      {/* Zoom-specific header */}
      <Card style={{ marginBottom: 16 }}>
        <Space direction="vertical" style={{ width: '100%' }}>
          <Space>
            <Title level={3} style={{ margin: 0 }}>ðŸ“¹ Zoom Meeting Assistant</Title>
            <Badge 
              status={isConnected ? 'success' : 'error'} 
              text={isConnected ? 'Connected' : 'Disconnected'} 
            />
          </Space>
          
          <Space wrap>
            <Text type="secondary">Platform: Zoom</Text>
            {meetingInfo && (
              <>
                <Text type="secondary">â€¢</Text>
                <Text type="secondary">Meeting ID: {meetingInfo.meetingUUID}</Text>
                <Text type="secondary">â€¢</Text>
                <Text type="secondary">{meetingInfo.participantCount} participants</Text>
              </>
            )}
          </Space>
          
          <Space wrap>
            <Text style={{ fontSize: 12 }}>âœ… Participant tracking</Text>
            <Text style={{ fontSize: 12 }}>âœ… Screen share detection</Text>
            <Text style={{ fontSize: 12 }}>âœ… Recording status</Text>
            <Text style={{ fontSize: 12 }}>âœ… Breakout rooms</Text>
          </Space>
        </Space>
      </Card>

      <SuggestionPanel platform="zoom" />
    </div>
  );
}
