import { useEffect, useState } from 'react';
import { Card, Badge, Space, Typography } from 'antd';
import * as microsoftTeams from '@microsoft/teams-js';
import SuggestionPanel from '../components/SuggestionPanel';
import { getPlatformFeatures } from '../utils/mockData';

const { Text, Title } = Typography;

export default function TeamsApp() {
  const [teamsContext, setTeamsContext] = useState<any>(null);
  const [isConnected, setIsConnected] = useState(false);
  
  useEffect(() => {
    microsoftTeams.app.initialize().then(() => {
      setIsConnected(true);
      console.log('Teams initialized');
      
      // Simulate getting Teams context
      setTimeout(() => {
        setTeamsContext({
          meetingId: 'teams-meeting-456',
          chatId: 'team-chat-789',
          userRole: 'organizer',
          meetingType: 'Scheduled'
        });
      }, 1000);
    }).catch(error => {
      console.error('Teams initialization failed:', error);
    });
  }, []);

  const platformFeatures = getPlatformFeatures('teams');

  return (
    <div style={{ padding: 16 }}>
      {/* Teams-specific header */}
      <Card style={{ marginBottom: 16 }}>
        <Space direction="vertical" style={{ width: '100%' }}>
          <Space>
            <Title level={3} style={{ margin: 0 }}>ðŸ’¼ Microsoft Teams Assistant</Title>
            <Badge 
              status={isConnected ? 'success' : 'error'} 
              text={isConnected ? 'Connected' : 'Disconnected'} 
            />
          </Space>
          
          <Space wrap>
            <Text type="secondary">Platform: Microsoft Teams</Text>
            {teamsContext && (
              <>
                <Text type="secondary">â€¢</Text>
                <Text type="secondary">Meeting: {teamsContext.meetingId}</Text>
                <Text type="secondary">â€¢</Text>
                <Text type="secondary">Role: {teamsContext.userRole}</Text>
              </>
            )}
          </Space>
          
          <Space wrap>
            <Text style={{ fontSize: 12 }}>âœ… Calendar integration</Text>
            <Text style={{ fontSize: 12 }}>âœ… Channel integration</Text>
            <Text style={{ fontSize: 12 }}>âœ… Meeting context</Text>
            <Text style={{ fontSize: 12 }}>âœ… Participant data</Text>
          </Space>
        </Space>
      </Card>

      <SuggestionPanel platform="teams" />
    </div>
  );
}
