import React, { useState, useEffect } from 'react';
import { Tabs, Card, Button, Space, Badge, Typography, Tooltip } from 'antd';
import { 
  QuestionCircleOutlined, 
  BulbOutlined, 
  RobotOutlined,
  CopyOutlined,
  HeartOutlined,
  ReloadOutlined
} from '@ant-design/icons';
import { 
  generateMeetingContext, 
  getQuestionsForMeeting, 
  getCuesForStage, 
  mockAISuggestions,
  MeetingContext,
  Question,
  Cue
} from '../utils/mockData';

const { Text } = Typography;

interface CompactSuggestionPanelProps {
  platform: 'zoom' | 'teams' | 'meet';
}

export default function CompactSuggestionPanel({ 
  platform 
}: CompactSuggestionPanelProps) {
  const [meetingContext, setMeetingContext] = useState<MeetingContext | null>(null);
  const [questions, setQuestions] = useState<Question[]>([]);
  const [cues, setCues] = useState<Cue[]>([]);
  const [activeTab, setActiveTab] = useState('1');
  const [favorites, setFavorites] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const context = generateMeetingContext(platform);
    setMeetingContext(context);
    setQuestions(getQuestionsForMeeting(context.type, 'start').slice(0, 4));
    setCues(getCuesForStage(context.stage).slice(0, 3));
    setLoading(false);
  }, [platform]);

  const handleCopyQuestion = (text: string) => {
    navigator.clipboard.writeText(text);
  };

  const handleToggleFavorite = (id: string) => {
    setFavorites(prev => 
      prev.includes(id) ? prev.filter(f => f !== id) : [...prev, id]
    );
  };

  if (loading) return <div style={{ padding: 20, textAlign: 'center' }}>Loading...</div>;

  return (
    <Tabs 
      activeKey={activeTab} 
      onChange={setActiveTab}
      size="small"
      style={{ height: '100%' }}
      items={[
        {
          key: '1',
          label: (
            <Space size={4}>
              <QuestionCircleOutlined />
              <span>Questions</span>
              <Badge count={questions.length} size="small" />
            </Space>
          ),
          children: (
            <div style={{ padding: '0 4px' }}>
              {questions.map(question => (
                <Card 
                  key={question.id}
                  size="small"
                  style={{ 
                    marginBottom: 8, 
                    fontSize: 12,
                    border: favorites.includes(question.id) ? '2px solid #ff4d4f' : undefined
                  }}
                  bodyStyle={{ padding: 12 }}
                >
                  <Space direction="vertical" size={4} style={{ width: '100%' }}>
                    <Space style={{ width: '100%', justifyContent: 'space-between' }}>
                      <Text style={{ fontSize: 13, fontWeight: 500 }}>
                        {question.icon} {question.text}
                      </Text>
                      <Space size={4}>
                        <Tooltip title="Copy">
                          <Button
                            type="text"
                            size="small"
                            icon={<CopyOutlined style={{ fontSize: 10 }} />}
                            onClick={() => handleCopyQuestion(question.text)}
                            style={{ padding: 2 }}
                          />
                        </Tooltip>
                        <Tooltip title="Favorite">
                          <Button
                            type="text"
                            size="small"
                            icon={<HeartOutlined style={{ 
                              fontSize: 10,
                              color: favorites.includes(question.id) ? '#ff4d4f' : undefined
                            }} />}
                            onClick={() => handleToggleFavorite(question.id)}
                            style={{ padding: 2 }}
                          />
                        </Tooltip>
                      </Space>
                    </Space>
                    <Text type="secondary" style={{ fontSize: 11 }}>
                      {question.category} â€¢ {question.priority}
                    </Text>
                  </Space>
                </Card>
              ))}
            </div>
          )
        },
        {
          key: '2',
          label: (
            <Space size={4}>
              <BulbOutlined />
              <span>Cues</span>
              <Badge count={cues.length} size="small" />
            </Space>
          ),
          children: (
            <div style={{ padding: '0 4px' }}>
              {cues.map(cue => (
                <Card 
                  key={cue.id}
                  size="small"
                  style={{ marginBottom: 8 }}
                  bodyStyle={{ padding: 12 }}
                >
                  <Space direction="vertical" size={4} style={{ width: '100%' }}>
                    <Space>
                      <span style={{ fontSize: 14 }}>{cue.icon}</span>
                      <Text strong style={{ fontSize: 13 }}>{cue.title}</Text>
                    </Space>
                    <Text style={{ fontSize: 12 }}>{cue.content}</Text>
                    <Text type="secondary" style={{ fontSize: 10 }}>
                      {cue.category}
                    </Text>
                  </Space>
                </Card>
              ))}
            </div>
          )
        },
        {
          key: '3',
          label: (
            <Space size={4}>
              <RobotOutlined />
              <span>AI</span>
              <Badge count={2} size="small" status="processing" />
            </Space>
          ),
          children: (
            <div style={{ padding: '0 4px' }}>
              {mockAISuggestions.slice(0, 2).map(suggestion => (
                <Card 
                  key={suggestion.id}
                  size="small"
                  style={{ marginBottom: 8 }}
                  bodyStyle={{ padding: 12 }}
                >
                  <Space direction="vertical" size={4} style={{ width: '100%' }}>
                    <Space>
                      <Badge status={suggestion.priority === 'high' ? 'error' : 'processing'} />
                      <Text strong style={{ fontSize: 12 }}>{suggestion.title}</Text>
                    </Space>
                    <Text style={{ fontSize: 11 }}>{suggestion.description}</Text>
                    <Button size="small" type="primary" style={{ fontSize: 10, height: 24 }}>
                      Apply
                    </Button>
                  </Space>
                </Card>
              ))}

              <Card style={{ textAlign: 'center', marginTop: 12 }} bodyStyle={{ padding: 16 }}>
                <Text type="secondary" style={{ fontSize: 11 }}>
                  ðŸ§  AI analyzing meeting...
                </Text>
              </Card>
            </div>
          )
        }
      ]}
    />
  );
}
